import React, { useState, useEffect } from 'react';
import './Deals.css';

const Deals = () => {
  const [wishlist, setWishlist] = useState({});
  const [time, setTime] = useState(new Date());

  const toggleWishlist = (id) => {
    setWishlist((prev) => ({
      ...prev,
      [id]: !prev[id],
    }));
  };

  useEffect(() => {
    const timer = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  const renderStars = (count) => {
    return '★'.repeat(count) + '☆'.repeat(5 - count);
  };

  const products = [
    {
      id: 1,
      img: 'https://5.imimg.com/data5/SELLER/Default/2022/1/JY/KA/LP/38863810/jelly-dryfruit-mixed-500x500.jpeg',
      category: 'Fruits',
      title: 'Mixed Fruits Chocolates',
      price: 20,
      oldPrice: 30,
      rating: 4,
      pack: '1 Pack',
      stock: '',
      label: 'NEW',
    },
    {
      id: 2,
      img: 'https://www.shutterstock.com/image-photo/apple-juice-glass-apples-600nw-2518840881.jpg',
      category: 'Juice',
      title: 'Organic Apple Juice Pack',
      price: 15,
      oldPrice: '',
      rating: 4,
      pack: '100 ml',
      stock: '3 Left',
      label: '',
    },
     {
    id: 3,
    img: 'data:image/jpeg;base64,',
    category: 'Shoes',
    title: 'Stylish Sports Shoes',
    price: 39,
    oldPrice: 49,
    rating: 5,
    pack: 'Size 7-10',
    stock: '',
    label: 'TRENDING',
  },
  {
    id: 4,
    img: 'data:image/jpeg;base64,',
    category: 'Cooked',
    title: 'Spicy Chicken Biryani',
    price: 149,
    oldPrice: 179,
    rating: 4,
    pack: '500g Box',
    stock: 'Out Of Stock',
    label: 'HOT',
  },
  {
    id: 5,
    img: 'data:image/jpeg;base64,',
    category: 'Pan',
    title: 'Meetha Pan Delight',
    price: 25,
    oldPrice: '',
    rating: 3,
    pack: '1 Piece',
    stock: '10 Left',
    label: '',
  },
  ];

  return (
    <section className="deals">
      <div className="deals-header">
        <p className="time">🕒 {time.toLocaleTimeString()}</p>
        <h2>
          Today's <span className="deal-highlight">Deals</span>
        </h2>
      </div>

      <div className="deal-cards">
        {products.map((item) => (
          <div
            key={item.id}
            className={`deal-card ${item.stock === 'Out Of Stock' ? 'out-stock' : ''}`}
          >
            {item.label && <div className="deal-label">{item.label}</div>}
            <img src={item.img} alt={item.title} />
            <div className="deal-actions">
              <span
                className={`wishlist ${wishlist[item.id] ? 'active' : ''}`}
                onClick={() => toggleWishlist(item.id)}
              >
                {wishlist[item.id] ? '❤️' : '🤍'}
              </span>
            </div>
            <div className="deal-info">
              <p className="deal-category">{item.category}</p>
              <h4>{item.title}</h4>
              <div className="stars">{renderStars(item.rating)}</div>
              <div className="price-row">
                <span className="price">₹{item.price}</span>
                {item.oldPrice && (
                  <span className="old-price">₹{item.oldPrice}</span>
                )}
              </div>
              <p className="pack">{item.pack}</p>
              {item.stock && (
                <p className={`stock ${item.stock === 'Out Of Stock' ? 'out' : 'left'}`}>
                  {item.stock}
                </p>
              )}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Deals;
